import Link from 'next/link';

const PostLink = props => {
  const { postTitle, postId } = props;
  return (
    <li>
      {/* <Link href={`/post?title=${postTitle}`}> */}
      {/* `href` is the path to post folder */}
      <Link href="/post/[id]" as={`/post/${postId}`}>
        <a>{postTitle}</a>
      </Link>
    </li>
  )
};

export default PostLink;
