import Link from 'next/link';

import axios from 'axios';

const pageTitleStyle = {
  color: 'green',
};

const filmTitleStyle = {
  color: 'red',
};

const filmStyle = {
  margin: '0 auto',
  textAlign: 'center',
}

const BatmanShows = props => {
  return (
    <div>
      <h2 style={pageTitleStyle}>Batman Shows</h2>
      {props.shows.map(show => {
        {console.log(777, show)}
        return (<div key={show.id} style={filmStyle}>
          <h3 style={filmTitleStyle}>
            {show.name}
          </h3>
          <div>
            <img src={show.image.medium}/>
          </div>
          <div>
            <span>Language: {show.language}</span>
          </div>
          <div>
            <p
              style={{
                textAlign: 'left',
                color: 'rgba(51, 51, 51, 0.8)',
                fontSize: 15,
                padding: '1rem'
                }}>
                About the show: {show.summary.replace(/<[/]?[pb]>/g, '')}
            </p>
          </div>
          <div>
            <span> Link: </span>
            <Link href="/show/[id]" as={`/show/${show.name}`}>
              <a>{show.name}</a>
            </Link>
          </div>
        </div>)
      })}
    </div>
  )

};

BatmanShows.getInitialProps = async function(){
  console.log(333333333)
  // const response = await fetch('https://api.tvmaze.com/search/shows?q=batman');
  // const shows = response.json();
  // console.log(11111, response);
  const data = await axios.get('https://api.tvmaze.com/search/shows?q=batman');
  const shows = data.data;
  // console.log(8888, data.data)
  // return {
  //   shows: shows.map(film => film.show)
  // }
  return {
    shows: shows.map(film => film.show)
}
}

// export default Layout(BatmanShows);
export default BatmanShows;
